class ArgumentParser:
    """
    This is a class for parsing command line arguments to a dictionary.
    """

    def __init__(self):
        """
        Initialize the fields.
        self.arguments is a dict that stores the args in a command line
        self.requried is a set that stores the required arguments
        self.types is a dict that stores type of every arguments.
        >>> parser.arguments
        {'key1': 'value1', 'option1': True}
        >>> parser.required
        {'arg1'}
        >>> parser.types
        {'arg1': 'type1'}
        """
        self.arguments = {}
        self.required = set()
        self.types = {}

    def parse_arguments(self, command_string):
        """Parses command line arguments, checks for missing required arguments, and stores results in a dictionary."""

    def get_argument(self, key):
        """Retrieves and returns the value of a specified argument from the arguments dictionary."""

    def add_argument(self, arg, required=False, arg_type=str):
        """The method adds an argument to the parser, specifying its name, requirement status, and type."""

    def _convert_type(self, arg, value):
        """Converts input value to specified type in self.types or returns original value if conversion fails."""

