class AssessmentSystem:
    """
    This is a class as an student assessment system, which supports add student, add course score, calculate GPA, and other functions for students and courses.
    """

    def __init__(self):
        """
        Initialize the students dict in assessment system.
        """
        self.students = {}

    def add_student(self, name, grade, major):
        """Adds a new student to the system with specified name, grade, and major information."""

    def add_course_score(self, name, course, score):
        """Adds a course score for a specific student in the system's student records."""

    def get_gpa(self, name):
        """Calculates and returns a student's average grade if they exist and have course grades."""

    def get_all_students_with_fail_course(self):
        """Returns a list of student names who have scored below 60 in any course."""

    def get_course_average(self, course):
        """Calculates and returns the average score for a specified course, or None if no records exist."""

    def get_top_student(self):
        """Calculates GPAs for all students and returns the name of the student with the highest GPA."""

