def main():
    N, K = map(int, input().split())
    groups = list(map(int, input().split()))
    empty_seats = K
    starts = 0

    for group in groups:
        if group > empty_seats:
            starts += 1
            empty_seats = K
        empty_seats -= group

    if empty_seats < K:
        starts += 1

    print(starts)

if __name__ == "__main__":
    main()
