class Solution:
    def longestMonotonicSubarray(self, nums: List[int]) -> int:
        n = len(nums)
        if n <= 1:
            return n

        max_len = 1
        increasing = decreasing = 1

        for i in range(1, n):
            if nums[i] > nums[i-1]:
                increasing += 1
                decreasing = 1
            elif nums[i] < nums[i-1]:
                decreasing += 1
                increasing = 1
            else:
                increasing = decreasing = 1

            max_len = max(max_len, max(increasing, decreasing))

        return max_len
