class Solution:
    def numberOfSpecialChars(self, word: str) -> int:
        # Create an empty set to store special characters
        special_chars = set()

        # Iterate through each character in the word
        for char in set(word):
            # Check if the character appears in both uppercase and lowercase
            if char.lower() in word and char.upper() in word:
                special_chars.add(char.lower())

        # Return the length of the set of special characters
        return len(special_chars)
