class Solution:
    def findPermutationDifference(self, s: str, t: str) -> int:
        # Create a dictionary to store the indices of characters in s
        s_indices = {char: index for index, char in enumerate(s)}

        # Calculate the permutation difference
        permutation_diff = 0
        for index, char in enumerate(t):
            permutation_diff += abs(index - s_indices[char])

        return permutation_diff
