class Solution:
    def minimumAverage(self, nums: List[int]) -> float:
        averages = []
        nums.sort()
        n = len(nums)

        for i in range(n // 2):
            minElement = nums.pop(0)
            maxElement = nums.pop()
            averages.append((minElement + maxElement) / 2)

        return min(averages)
