class Solution:
    def winningPlayerCount(self, n: int, pick: List[List[int]]) -> int:
        # Create a dictionary to store the count of balls for each player
        player_balls = {i: {} for i in range(n)}

        # Count the balls for each player
        for p, c in pick:
            player_balls[p][c] = player_balls[p].get(c, 0) + 1

        # Check the winning condition for each player
        win_count = 0
        for i in range(n):
            # Check if the player has at least (i + 1) balls of the same color
            if any(count >= i + 1 for count in player_balls[i].values()):
                win_count += 1

        return win_count
