import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

N = int(input())
points = []
for _ in range(N):
    x, y = map(int, input().split())
    points.append((x, y))

for i in range(N):
    x1, y1 = points[i]
    max_dist = 0
    farthest_point = None
    for j in range(N):
        if i != j:
            x2, y2 = points[j]
            dist = distance(x1, y1, x2, y2)
            if dist > max_dist:
                max_dist = dist
                farthest_point = j + 1
    print(farthest_point)
