class Solution:
    def occurrencesOfElement(self, nums: List[int], queries: List[int], x: int) -> List[int]:
        occurrences = []
        count = 0
        for num in nums:
            if num == x:
                count += 1
            occurrences.append(count)

        result = []
        for query in queries:
            if query > occurrences[-1]:
                result.append(-1)
            else:
                left = 0
                right = len(occurrences)
                while left < right:
                    mid = left + (right - left) // 2
                    if occurrences[mid] < query:
                        left = mid + 1
                    else:
                        right = mid
                if occurrences[left] == query:
                    result.append(left)
                else:
                    result.append(-1)

        return result
