class Solution:
    def valueAfterKSeconds(self, n: int, k: int) -> int:
        MOD = 10**9 + 7
        prev = [1] * n
        for _ in range(k):
            curr = [0] * n
            curr[0] = 1
            for i in range(1, n):
                curr[i] = (curr[i-1] + prev[i]) % MOD
            prev = curr
        return prev[-1]
