class Solution:
    def resultsArray(self, nums: List[int], k: int) -> List[int]:
        n = len(nums)
        results = []

        for i in range(n - k + 1):
            subarray = nums[i:i+k]
            is_consecutive = all(subarray[j] == subarray[0] + j for j in range(k))
            is_sorted = subarray == sorted(subarray)

            if is_consecutive and is_sorted:
                results.append(subarray[-1])
            else:
                results.append(-1)

        return results
