class Solution:
    def stringHash(self, s: str, k: int) -> str:
        n = len(s)
        result = ""
        for i in range(0, n, k):
            substring = s[i:i+k]
            hashsum = sum(ord(char) - ord('a') for char in substring)
            hashedChar = chr(hashsum % 26 + ord('a'))
            result += hashedChar
        return result
