class Solution:
    def countOfSubstrings(self, word: str, k: int) -> int:
        def has_all_vowels(substring):
            vowels = set('aeiou')
            return all(v in set(substring) for v in vowels)

        def count_consonants(substring):
            return sum(1 for c in substring if c not in set('aeiou'))

        result = 0
        n = len(word)
        for start in range(n):
            for end in range(start + 5, n + 1):
                substring = word[start:end]
                if has_all_vowels(substring) and count_consonants(substring) == k:
                    result += 1

        return result
