class Solution:
    def minStartingIndex(self, s: str, pattern: str) -> int:
        m, n = len(s), len(pattern)

        def is_almost_equal(a, b):
            diff = 0
            for i in range(n):
                if a[i] != b[i]:
                    diff += 1
                    if diff > 1:
                        return False
            return True

        for i in range(m - n + 1):
            if is_almost_equal(s[i:i+n], pattern):
                return i

        return -1
