from collections import defaultdict

bag = defaultdict(int)
queries = []

Q = int(input())
for _ in range(Q):
    query = input().split()
    queries.append(query)

for query in queries:
    if query[0] == '1':
        x = int(query[1])
        bag[x] += 1
    elif query[0] == '2':
        x = int(query[1])
        bag[x] -= 1
        if bag[x] == 0:
            del bag[x]
    else:
        print(len(bag))
